			if (args.containsKey("min_y")) {
				minY = (Integer)args.get("min_y");
			}
			if (args.containsKey("max_y")) {
				maxY = (Integer)args.get("max_y");
			}
		}

		String world = "world";
		world = (String)args.get("world");

		return new BuildLimit(world, type, minY, maxY);
	}

